import React, { useState, useEffect } from 'react';
import CourseDetails from '../../components/CourseDetails/CourseDetails';
import CourseForm from '../../components/courseForm/CourseForm';
import Calendar from '../../components/calendar/calendar';
import { getCourses, createCourse, updateCourse, deleteCourse } from '../../api/courses';
import styles from './Planner.module.css';

const Planner = () => {
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await getCourses();
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  const handleSelectCourse = (course) => {
    setSelectedCourse(course);
    setIsEditing(false);
  };

  const handleCreateCourse = async (courseData) => {
    try {
      await createCourse(courseData);
      fetchCourses();
      setIsEditing(false);
    } catch (error) {
      console.error('Error creating course:', error);
    }
  };

  const handleUpdateCourse = async (courseData) => {
    try {
      await updateCourse(selectedCourse.id, courseData);
      fetchCourses();
      setSelectedCourse(null);
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating course:', error);
    }
  };

  const handleDeleteCourse = async (id) => {
    try {
      await deleteCourse(id);
      fetchCourses();
      if (selectedCourse && selectedCourse.id === id) {
        setSelectedCourse(null);
      }
    } catch (error) {
      console.error('Error deleting course:', error);
    }
  };

  return (
    <div className={styles.plannerContainer}>
      <div>
        <h2>Your Courses</h2>
        <CourseList
          courses={courses}
          onSelect={handleSelectCourse}
          onDelete={handleDeleteCourse}
        />
        <button className={styles.addButton} onClick={() => setIsEditing(true)}>Add New Course</button>
      </div>
      <div>
        {isEditing ? (
          <CourseForm
            onSubmit={selectedCourse ? handleUpdateCourse : handleCreateCourse}
            initialData={selectedCourse}
          />
        ) : (
          <CourseDetails course={selectedCourse} />
        )}
        <Calendar courses={courses} />
      </div>
    </div>
  );
};

export default Planner;
